import React, { useState } from 'react';

const LoginForm = ({ setUserProfile }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = () => {
    // Пример авторизации (в реальности должна быть проверка на сервере)
    const user = { email }; // В реальности данные должны быть получены с сервера
    localStorage.setItem('userProfile', JSON.stringify(user));
    setUserProfile(user); // Обновляем профиль пользователя
  };

  return (
    <div>
      <h2>Войти</h2>
      <input
        type="email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        placeholder="Электронная почта"
      />
      <input
        type="password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        placeholder="Пароль"
      />
      <button onClick={handleLogin}>Войти</button>
    </div>
  );
};

export default LoginForm;
