import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './LoginForm.css';

const LoginForm = ({ setUserProfile }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = (e) => {
    e.preventDefault();

    // Псевдопроверка данных пользователя
    if (email === 'user@example.com' && password === 'password123') {
      const userProfile = { email };  // Имитация профиля пользователя
      localStorage.setItem('userProfile', JSON.stringify(userProfile));  // Сохранение в localStorage
      setUserProfile(userProfile);
      navigate('/');
    } else {
      alert('Неверный логин или пароль');
    }
  };

  return (
    <div className="login-form">
      <h2>Войти</h2>
      <form onSubmit={handleLogin}>
        <div>
          <label>Email</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Пароль</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit">Войти</button>
      </form>
    </div>
  );
};

export default LoginForm;
