import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './LoginForm.css';

const LoginForm = ({ setUserProfile }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();

    // Получаем массив пользователей из localStorage
    const users = JSON.parse(localStorage.getItem('users')) || [];

    // Ищем пользователя с введенными email и паролем
    const user = users.find(
      (user) => user.email === email && user.password === password
    );

    if (user) {
      // Если пользователь найден, сохраняем его профиль в localStorage
      localStorage.setItem('userProfile', JSON.stringify(user));
      setUserProfile(user); // Обновляем состояние пользователя
      navigate('/profile'); // Перенаправляем на страницу профиля
    } else {
      // Если пользователь не найден, выводим ошибку
      setErrorMessage('Неверный email или пароль');
    }
  };

  return (
    <div className="login-form-container">
      <h2>Вход в профиль</h2>
      {errorMessage && <div className="error-message">{errorMessage}</div>}
      <form onSubmit={handleSubmit}>
        <div className="form-item">
          <label htmlFor="email">Email:</label>
          <input
            id="email"
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Введите ваш email"
            required
          />
        </div>
        <div className="form-item">
          <label htmlFor="password">Пароль:</label>
          <input
            id="password"
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Введите ваш пароль"
            required
          />
        </div>
        <button type="submit" className="login-button">
          Войти
        </button>
      </form>
    </div>
  );
};

export default LoginForm;
