import React from 'react';
import { useNavigate } from 'react-router-dom';

const UserProfile = ({ userProfile, updateUserProfile }) => {
  const navigate = useNavigate();

  const handleLogout = () => {
    // Очистить данные профиля из localStorage
    localStorage.removeItem('userProfile');
    // Обновить состояние профиля
    updateUserProfile(null);
    // Перенаправить пользователя на страницу входа
    navigate('/login');
  };

  return (
    <div className="user-profile">
      <h2>Добро пожаловать, {userProfile.name}!</h2>
      <p>Email: {userProfile.email}</p>

      <div className="order-history">
        <h3>Ваши заказы</h3>
        {/* Здесь можно добавить логику отображения заказов */}
        <ul>
          {userProfile.orders && userProfile.orders.length > 0 ? (
            userProfile.orders.map((order, index) => (
              <li key={index}>
                <p>Заказ #{order.id}</p>
                <p>Дата: {order.date}</p>
              </li>
            ))
          ) : (
            <p>У вас нет заказов.</p>
          )}
        </ul>
      </div>

      <div className="profile-actions">
        <button onClick={() => navigate('/change-password')}>Изменить пароль</button>
        <button onClick={handleLogout} className="logout-btn">Выйти</button>
      </div>
    </div>
  );
};

export default UserProfile;
