import React, { useEffect, useState } from 'react';
import ProductCard from './ProductCard';

const ProductList = ({ addToCart, addToWishlist }) => {
  const [products, setProducts] = useState([]);

  // Получаем данные из products.json, расположенного в папке public
  useEffect(() => {
    fetch('/products.json')
      .then((response) => response.json())
      .then((data) => setProducts(data))
      .catch((error) => console.error('Error fetching products:', error));
  }, []);

  return (
    <div className="product-list">
      {products.map((product) => (
        <ProductCard
          key={product.id}
          product={product}
          addToCart={addToCart} // Передаём функцию добавления в корзину
          addToWishlist={addToWishlist} // Передаём функцию добавления в избранное
        />
      ))}
    </div>
  );
};

export default ProductList;
