import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, Navigate } from 'react-router-dom';
import Cart from './components/Cart';
import UserProfile from './components/UserProfile';
import LoginForm from './components/LoginForm';
import Notification from './components/Notification';

const App = () => {
  const [userProfile, setUserProfile] = useState(null);
  const [cartItems, setCartItems] = useState([]);
  const [notification, setNotification] = useState('');

  // Загружаем данные из localStorage
  useEffect(() => {
    const storedUserProfile = JSON.parse(localStorage.getItem('userProfile'));
    if (storedUserProfile) {
      setUserProfile(storedUserProfile);
    }
  }, []);

  // Функция для отображения уведомлений
  const showNotification = (message) => {
    setNotification(message);
    setTimeout(() => setNotification(''), 3000);
  };

  return (
    <Router>
      <div className="header">
        <h1>Магазин</h1>
        <div className="navigation">
          <Link to="/">Главная</Link>
          <Link to="/cart">Корзина</Link>
          {userProfile ? (
            <Link to="/profile">Профиль</Link>
          ) : (
            <Link to="/login">Войти</Link>
          )}
        </div>
      </div>

      {notification && (
        <Notification message={notification} onClose={() => setNotification('')} />
      )}

      <Routes>
        <Route path="/" element={<div>Главная страница</div>} />
        <Route
          path="/cart"
          element={
            <Cart
              cartItems={cartItems}
              removeFromCart={(id) => setCartItems(cartItems.filter(item => item.id !== id))}
              setUserProfile={setUserProfile} // Передаем setUserProfile как функцию
              showNotification={showNotification}
              userProfile={userProfile}
            />
          }
        />
        <Route path="/login" element={<LoginForm setUserProfile={setUserProfile} />} />
        <Route
          path="/profile"
          element={userProfile ? <UserProfile userProfile={userProfile} /> : <Navigate to="/login" />}
        />
      </Routes>
    </Router>
  );
};

export default App;
