import React, { useEffect } from 'react';
import './Cart.css';
import { addOrderToUserHistory } from './utils/userUtils'; // Импорт функции добавления заказа

const Cart = ({ cartItems, removeFromCart, updateCartItemQuantity, clearCart, showNotification, userProfile, setUserProfile }) => {
  
  // Используем useEffect для обновления профиля пользователя из localStorage
  useEffect(() => {
    // Проверяем, существует ли уже профиль пользователя в localStorage
    const storedUserProfile = JSON.parse(localStorage.getItem('userProfile'));
    if (storedUserProfile && !userProfile) {
      setUserProfile(storedUserProfile); // Обновляем профиль пользователя
    }
  }, [userProfile, setUserProfile]);  // Проверяем зависимости: обновляем профиль, если он еще не установлен

  const calculateTotal = () => {
    return cartItems.reduce((total, item) => total + item.price * item.quantity, 0);
  };

  const handleCheckout = () => {
    if (cartItems.length > 0) {
      if (!userProfile) {
        showNotification('Вы должны войти в систему для оформления заказа!');
        return;
      }

      const newOrder = {
        id: Date.now(), // Уникальный идентификатор заказа
        date: new Date().toLocaleDateString(),
        items: cartItems,
        total: calculateTotal(),
      };

      // Добавляем заказ в историю пользователя
      addOrderToUserHistory(newOrder); // Передаем новый заказ, включая детали

      // Очистка корзины
      clearCart();

      // Уведомление о успешном оформлении заказа
      showNotification('Заказ успешно оформлен!');
    } else {
      showNotification('Ваша корзина пуста!'); // Уведомление для пустой корзины
    }
  };

  return (
    <div className="cart-details">
      <h3>Содержимое корзины</h3>
      {cartItems.length === 0 ? (
        <p>Корзина пуста</p>
      ) : (
        <ul>
          {cartItems.map((item) => (
            <li key={item.id} className="cart-item">
              <img src={item.image} alt={item.name} width="50" />
              <div className="item-details">
                <span>
                  {item.name} — {item.price} ₽, размер: {item.size}
                </span>
                <div className="quantity-controls">
                  <button
                    onClick={() => updateCartItemQuantity(item.id, -1)}
                    className="quantity-button"
                  >
                    -
                  </button>
                  <span>{item.quantity}</span>
                  <button
                    onClick={() => updateCartItemQuantity(item.id, 1)}
                    className="quantity-button"
                  >
                    +
                  </button>
                </div>
              </div>
              <button
                onClick={() => removeFromCart(item.id)}
                className="remove-button"
              >
                Удалить
              </button>
            </li>
          ))}
        </ul>
      )}
      {cartItems.length > 0 && (
        <div className="total">
          <h4>Итоговая стоимость: {calculateTotal()} ₽</h4>
          <button onClick={handleCheckout} className="checkout-button">
            Оформить заказ
          </button>
        </div>
      )}
    </div>
  );
};

export default Cart;
