import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Link, Navigate } from 'react-router-dom';
import ProductCard from './components/ProductCard';
import Cart from './components/Cart';
import ProductDetail from './components/ProductDetail';
import UserProfile from './components/UserProfile';
import ChangePassword from './components/ChangePassword';
import Wishlist from './components/Wishlist';
import Reviews from './components/Reviews';
import StoreInfo from './components/StoreInfo';
import Notification from './components/Notification';
import LoginForm from './components/LoginForm';
import Register from './components/Register';
import AdminPanel from './components/AdminPanel';
import './App.css';

const App = () => {
  const [products, setProducts] = useState([]); // Состояние для продуктов
  const [selectedCategory, setSelectedCategory] = useState('Все');
  const [cartItems, setCartItems] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [orderHistory, setOrderHistory] = useState([]);
  const [userProfile, setUserProfile] = useState(null);
  const [wishlist, setWishlist] = useState([]);
  const [notification, setNotification] = useState('');
  const [error, setError] = useState(''); // Состояние для ошибки

  // Загрузка продуктов с сервера
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/products');
        if (!response.ok) {
          throw new Error('Ошибка при загрузке продуктов');
        }
        const data = await response.json();
        setProducts(data);
      } catch (err) {
        console.error('Ошибка:', err);
        setError('Не удалось загрузить продукты. Попробуйте позже.');
      }
    };

    fetchProducts();
  }, []);

  // Функция для отображения уведомлений
  const showNotification = (message) => {
    setNotification(message);
    setTimeout(() => setNotification(''), 3000);
  };

  // Фильтрация продуктов
  const filteredProducts = products.filter((product) => {
    const matchesCategory = selectedCategory === 'Все' || product.category === selectedCategory;
    const matchesSearchTerm = product.name.toLowerCase().includes(searchTerm.toLowerCase());
    return matchesCategory && matchesSearchTerm;
  });

  // Добавление товара в корзину
  const addToCart = (product) => {
    const existingProduct = cartItems.find((item) => item.id === product._id);
    if (existingProduct) {
      setCartItems(
        cartItems.map((item) =>
          item.id === product._id
            ? { ...item, quantity: item.quantity + 1 }
            : item
        )
      );
    } else {
      setCartItems([...cartItems, { ...product, id: product._id, quantity: 1 }]);
    }
    showNotification(`Товар ${product.name} добавлен в корзину!`);
  };

  // Удаление товара из корзины
  const removeFromCart = (id) => {
    setCartItems(cartItems.filter((item) => item.id !== id));
    showNotification('Товар удален из корзины!');
  };

  return (
    <Router>
      <div className="header">
        <h1 style={{ textAlign: 'center' }}>Магазин одежды и обуви</h1>
        <div className="navigation">
          <Link to="/">Главная</Link>
          <Link to="/cart">Корзина</Link>
          <Link to="/wishlist">Избранное</Link>
          <Link to="/store-info">Информация о магазине</Link>
          {userProfile ? (
            <Link to="/profile">Профиль</Link>
          ) : (
            <>
              <Link to="/login">Войти</Link>
              <Link to="/register">Регистрация</Link>
            </>
          )}
        </div>
      </div>

      {notification && (
        <Notification
          message={notification}
          onClose={() => setNotification('')}
        />
      )}

      <Routes>
        <Route
          path="/"
          element={
            <div>
              {error && <div className="error-message">{error}</div>}
              <div className="search-container">
                <input
                  type="text"
                  className="search-input"
                  placeholder="Поиск по товарам..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
              </div>
              <div className="category-buttons">
                <button onClick={() => setSelectedCategory('Все')}>Все</button>
                <button onClick={() => setSelectedCategory('Одежда')}>Одежда</button>
                <button onClick={() => setSelectedCategory('Обувь')}>Обувь</button>
              </div>

              <div className="container">
                {filteredProducts.map((product) => (
                  <div key={product._id}>
                    <ProductCard
                      product={product}
                      addToCart={addToCart}
                      addToWishlist={(product) => {
                        if (!wishlist.find((item) => item._id === product._id)) {
                          setWishlist([...wishlist, product]);
                          showNotification(`Товар ${product.name} добавлен в избранное!`);
                        }
                      }}
                    />
                  </div>
                ))}
              </div>
            </div>
          }
        />
        <Route
          path="/product/:id"
          element={<ProductDetail products={products} addToCart={addToCart} />}
        />
        <Route
          path="/cart"
          element={
            <Cart
              cartItems={cartItems}
              removeFromCart={removeFromCart}
              updateCartItemQuantity={(id, delta) =>
                setCartItems((prevItems) =>
                  prevItems
                    .map((item) =>
                      item.id === id
                        ? { ...item, quantity: item.quantity + delta }
                        : item
                    )
                    .filter((item) => item.quantity > 0)
                )
              }
              clearCart={() => {
                setCartItems([]);
                showNotification('Корзина очищена!');
              }}
            />
          }
        />
        <Route
          path="/profile"
          element={
            userProfile ? (
              <UserProfile userProfile={userProfile} />
            ) : (
              <Navigate to="/login" />
            )
          }
        />
        <Route
          path="/admin"
          element={<AdminPanel />}
        />
      </Routes>
    </Router>
  );
};

export default App;
