import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Link, Navigate } from 'react-router-dom';
import ProductCard from './components/ProductCard';
import Cart from './components/Cart';
import ProductDetail from './components/ProductDetail';
import UserProfile from './components/UserProfile';
import ChangePassword from './components/ChangePassword';
import Wishlist from './components/Wishlist';
import Reviews from './components/Reviews';
import StoreInfo from './components/StoreInfo';
import Notification from './components/Notification';
import LoginForm from './components/LoginForm';
import Register from './components/Register';
import './App.css';

const App = () => {
  const [selectedCategory, setSelectedCategory] = useState('Все');
  const [cartItems, setCartItems] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [orderHistory, setOrderHistory] = useState([]);
  const [userProfile, setUserProfile] = useState(null);
  const [wishlist, setWishlist] = useState([]);
  const [notification, setNotification] = useState('');

  // Загружаем данные из localStorage
  useEffect(() => {
    const storedUserProfile = localStorage.getItem('userProfile');
    if (storedUserProfile) {
      try {
        setUserProfile(JSON.parse(storedUserProfile));
      } catch (error) {
        console.error('Ошибка при парсинге профиля пользователя из localStorage', error);
      }
    }

    const savedCartItems = localStorage.getItem('cartItems');
    const savedOrderHistory = localStorage.getItem('orderHistory');
    const savedWishlist = localStorage.getItem('wishlist');

    setCartItems(savedCartItems ? JSON.parse(savedCartItems) : []);
    setOrderHistory(savedOrderHistory ? JSON.parse(savedOrderHistory) : []);
    setWishlist(savedWishlist ? JSON.parse(savedWishlist) : []);
  }, []);

  // Функция для сохранения данных в localStorage
  const saveDataToLocalStorage = () => {
    localStorage.setItem('cartItems', JSON.stringify(cartItems));
    localStorage.setItem('orderHistory', JSON.stringify(orderHistory));
    localStorage.setItem('wishlist', JSON.stringify(wishlist));
    if (userProfile) {
      localStorage.setItem('userProfile', JSON.stringify(userProfile));  // Переходим к обновлению данных профиля
    }
  };

  // Функция для отображения уведомлений
  const showNotification = (message) => {
    setNotification(message);
    setTimeout(() => setNotification(''), 3000);
  };

  // Добавление товара в корзину
  const addToCart = (product) => {
    const existingProduct = cartItems.find(item => item.id === product.id);
    if (existingProduct) {
      setCartItems(cartItems.map(item =>
        item.id === product.id
          ? { ...item, quantity: item.quantity + 1 }
          : item
      ));
    } else {
      setCartItems([...cartItems, { ...product, quantity: 1 }]);
    }
    showNotification(`Товар ${product.name} добавлен в корзину!`);
    saveDataToLocalStorage();
  };

  // Обновление количества товара в корзине
  const updateCartItemQuantity = (id, delta) => {
    setCartItems(prevItems =>
      prevItems
        .map(item => (item.id === id ? { ...item, quantity: item.quantity + delta } : item))
        .filter(item => item.quantity > 0)
    );
    saveDataToLocalStorage();
  };

  // Добавление товара в список избранного
  const addToWishlist = (product) => {
    if (!wishlist.find(item => item.id === product.id)) {
      setWishlist([...wishlist, product]);
      showNotification(`Товар ${product.name} добавлен в избранное!`);
      saveDataToLocalStorage();
    }
  };

  // Удаление товара из корзины
  const removeFromCart = (id) => {
    setCartItems(cartItems.filter(item => item.id !== id));
    showNotification('Товар удален из корзины!');
    saveDataToLocalStorage();
  };

  // Обработка оформления заказа
  const handleOrder = () => {
    if (!userProfile) {
      showNotification('Пожалуйста, войдите в систему для оформления заказа');
      return;
    }

    const newOrder = {
      id: orderHistory.length + 1,
      items: cartItems,
      date: new Date().toLocaleDateString(),
    };

    setOrderHistory(prevOrderHistory => {
      const updatedOrderHistory = [...prevOrderHistory, newOrder];  // Добавляем новый заказ в конец массива
      saveDataToLocalStorage();  // Сохраняем обновлённые заказы в localStorage
      return updatedOrderHistory;
    });

    setCartItems([]);
    showNotification('Заказ оформлен!');
    saveDataToLocalStorage();  // Сохраняем обновлённую корзину в localStorage
  };

  // Функция для очистки корзины
  const clearCart = () => {
    setCartItems([]);  // Очистка корзины
    saveDataToLocalStorage();
    showNotification('Корзина очищена!');
  };

  // Обновление профиля пользователя
  const updateUserProfile = (profileData) => {
    setUserProfile(profileData);
    saveDataToLocalStorage();  // В этой функции уже есть сохранение userProfile
  };

  // Удаление товара из списка избранного
  const removeFromWishlist = (id) => {
    setWishlist(wishlist.filter(item => item.id !== id));
    saveDataToLocalStorage();
  };

  const products = [
    { id: 1, name: 'Футболка', price: 1500, image: `${process.env.PUBLIC_URL}/img/shirt.jpg`, category: 'Одежда', description: 'Качественная футболка из хлопка', sizeChart: 'Размерная сетка футболок' },
    { id: 2, name: 'Кроссовки', price: 5000, image: `${process.env.PUBLIC_URL}/img/sneakers.jpg`, category: 'Обувь', description: 'Удобные кроссовки для бега', sizeChart: 'Размерная сетка кроссовок' },
    { id: 3, name: 'Кроссовки 2', price: 10000, image: `${process.env.PUBLIC_URL}/img/sneaker.jpg`, category: 'Обувь', description: 'Стильные кроссовки для города', sizeChart: 'Размерная сетка кроссовок' },
  ];

  const filteredProducts = products.filter(product => {
    const matchesCategory = selectedCategory === 'Все' || product.category === selectedCategory;
    const matchesSearchTerm = product.name.toLowerCase().includes(searchTerm.toLowerCase());
    return matchesCategory && matchesSearchTerm;
  });

  return (
    <Router>
      <div className="header">
        <h1 style={{ textAlign: 'center' }}>Магазин одежды и обуви</h1>
        <div className="navigation">
          <Link to="/">Главная</Link>
          <Link to="/cart">Корзина</Link>
          <Link to="/wishlist">Избранное</Link>
          <Link to="/store-info">Информация о магазине</Link>
          {userProfile ? (
            <Link to="/profile">Профиль</Link>
          ) : (
            <>
              <Link to="/login">Войти</Link>
              <Link to="/register">Регистрация</Link>
            </>
          )}
        </div>
      </div>

      {notification && (
        <Notification 
          message={notification} 
          onClose={() => setNotification('')} 
        />
      )}

      <Routes>
        <Route path="/" element={
          <div>
            <div className="search-container">
              <input
                type="text"
                className="search-input"
                placeholder="Поиск по товарам..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>
            <div className="category-buttons">
              <button onClick={() => setSelectedCategory('Все')}>Все</button>
              <button onClick={() => setSelectedCategory('Одежда')}>Одежда</button>
              <button onClick={() => setSelectedCategory('Обувь')}>Обувь</button>
            </div>

            <div className="container">
              {filteredProducts.map(product => (
                <div key={product.id}>
                  <ProductCard 
                    product={product} 
                    addToCart={addToCart} 
                    addToWishlist={addToWishlist} 
                  />
                </div>
              ))}
            </div>
          </div>
        } />
        <Route path="/product/:id" element={<ProductDetail products={products} addToCart={addToCart} />} />
        
        <Route
          path="/cart"
          element={
            <Cart
              cartItems={cartItems}
              removeFromCart={removeFromCart}
              updateCartItemQuantity={updateCartItemQuantity}
              clearCart={clearCart}  // Передаем функцию clearCart
              showNotification={showNotification}
              userProfile={userProfile}
              setUserProfile={setUserProfile}
            />
          }
        />
        <Route path="/profile" element={userProfile ? <UserProfile userProfile={userProfile} updateUserProfile={updateUserProfile} /> : <Navigate to="/login" />} />
        <Route path="/wishlist" element={<Wishlist wishlist={wishlist} removeFromWishlist={removeFromWishlist} />} />
        <Route path="/change-password" element={<ChangePassword updateUserProfile={updateUserProfile} />} />
        <Route path="/reviews" element={<Reviews products={products} />} />
        <Route path="/store-info" element={<StoreInfo />} />
        <Route path="/login" element={<LoginForm setUserProfile={setUserProfile} />} />
        <Route path="/register" element={<Register />} />
      </Routes>
    </Router>
  );
};

export default App;
