import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './UserProfile.css';

const UserProfile = ({ userProfile, updateUserProfile }) => {
  const [orders] = useState([
    { id: 1, date: '2024-10-01', items: ['Футболка', 'Кроссовки'], total: 6500 },
    { id: 2, date: '2024-10-15', items: ['Куртка'], total: 8000 },
  ]);

  const navigate = useNavigate();

  const handleLogout = () => {
    // Очистка данных профиля из localStorage
    localStorage.removeItem('userProfile');
    // Обновить состояние профиля
    updateUserProfile(null);
    // Перенаправить на страницу входа
    navigate('/login');
  };

  return (
    <div className="user-profile-container">
      <h2>Ваш профиль</h2>
      <div className="profile-info">
        <p><strong>Имя:</strong> {userProfile.name}</p>
        <p><strong>Email:</strong> {userProfile.email}</p>
        <Link to="/change-password">Сменить пароль</Link>
      </div>

      <div className="order-history">
        <h3>История заказов</h3>
        {orders.length === 0 ? (
          <p>Вы не совершали заказов.</p>
        ) : (
          <ul>
            {orders.map(order => (
              <li key={order.id}>
                <p><strong>Заказ №{order.id}</strong></p>
                <p>Дата: {order.date}</p>
                <p>Товары: {order.items.join(', ')}</p>
                <p>Сумма: {order.total} ₽</p>
              </li>
            ))}
          </ul>
        )}
      </div>

      <div className="profile-actions">
        <button onClick={handleLogout} className="logout-btn">Выйти</button>
      </div>
    </div>
  );
};

export default UserProfile;
