// userUtils.js

/**
 * Получение профиля пользователя из localStorage
 * @returns {Object|null} Профиль пользователя или null, если профиль не найден
 */
export const getUserProfile = () => {
  try {
    const userProfile = localStorage.getItem('userProfile');
    return userProfile ? JSON.parse(userProfile) : null;
  } catch (error) {
    console.error('Ошибка при получении профиля пользователя из localStorage:', error);
    return null;
  }
};

/**
 * Сохранение профиля пользователя в localStorage
 * @param {Object} profile - Объект профиля пользователя
 */
export const saveUserProfile = profile => {
  try {
    localStorage.setItem('userProfile', JSON.stringify(profile));
  } catch (error) {
    console.error('Ошибка при сохранении профиля пользователя в localStorage:', error);
  }
};

/**
 * Добавление заказа в историю заказов пользователя
 * @param {Array} cartItems - Список товаров из корзины
 */
export const addOrderToUserHistory = cartItems => {
  if (!Array.isArray(cartItems) || cartItems.length === 0) {
    console.error('Корзина пуста или неверный формат данных');
    return;
  }
  const userProfile = getUserProfile();
  if (!userProfile) {
    console.error('Профиль пользователя не найден');
    return;
  }
  const newOrder = {
    id: Date.now(),
    // Уникальный идентификатор заказа
    date: new Date().toISOString().split('T')[0],
    // Текущая дата в формате YYYY-MM-DD
    items: cartItems.map(item => ({
      name: item.name,
      size: item.size,
      price: item.price,
      quantity: item.quantity
    })),
    total: cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0)
  };

  // Обновление истории заказов
