import React, { useState, useEffect } from 'react';
import './UserProfile.css';

// Функция для получения заказов пользователя с сервера (заглушка)
const fetchUserOrders = async (userId) => {
  // Здесь вы будете делать запрос к серверу, который вернет заказы пользователя
  return [
    { id: 1, items: ['Кроссовки', 'Футболка'], total: 3500, date: '2024-10-15' },
    { id: 2, items: ['Шорты'], total: 1500, date: '2024-10-01' }
  ]; 
};

const UserProfile = ({ userProfile, updateUserProfile }) => {
  const [name, setName] = useState(userProfile.name || '');
  const [email, setEmail] = useState(userProfile.email || '');
  const [password, setPassword] = useState('');
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    // Загрузка заказов пользователя при монтировании компонента
    const loadOrders = async () => {
      const fetchedOrders = await fetchUserOrders(userProfile.id);
      setOrders(fetchedOrders);
    };

    loadOrders();
  }, [userProfile.id]);

  const handleSave = () => {
    // Запрос на обновление профиля (добавьте API запрос)
    updateUserProfile({ name, email });
    alert('Профиль обновлен');
  };

  const handleChangePassword = () => {
    // Логика смены пароля (добавьте API запрос для обновления пароля)
    alert('Пароль успешно изменён');
  };

  return (
    <div className="user-profile">
      <h2>Профиль пользователя</h2>
      <form>
        <input type="text" value={name} onChange={(e) => setName(e.target.value)} placeholder="Имя" />
        <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Email" />
        <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Новый пароль" />
        <button type="button" onClick={handleSave}>Сохранить профиль</button>
        <button type="button" onClick={handleChangePassword}>Изменить пароль</button>
      </form>

      <div className="user-orders">
        <h3>Мои заказы</h3>
        {orders.length === 0 ? (
          <p>У вас нет заказов.</p>
        ) : (
          <ul>
            {orders.map((order) => (
              <li key={order.id}>
                <span>Дата заказа: {order.date}</span>
                <span>Товары: {order.items.join(', ')}</span>
                <span>Итоговая стоимость: {order.total} ₽</span>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default UserProfile;
