import React, { useEffect } from 'react';
import './Cart.css';

const Cart = ({
  cartItems,
  removeFromCart,
  updateCartItemQuantity,
  clearCart,
  showNotification,
  userProfile,
  setUserProfile,
}) => {

  // Эффект для обновления профиля пользователя
  useEffect(() => {
    const storedUserProfile = JSON.parse(localStorage.getItem('userProfile'));
    if (storedUserProfile && !userProfile) {
      setUserProfile(storedUserProfile); // Устанавливаем профиль пользователя из localStorage
    }
  }, [setUserProfile, userProfile]);

  const calculateTotal = () => {
    return cartItems.reduce((total, item) => total + item.price * item.quantity, 0);
  };

  const handleCheckout = async () => {
    if (cartItems.length > 0) {
      if (!userProfile) {
        showNotification('Вы должны войти в систему для оформления заказа!');
        return;
      }

      const newOrder = {
        items: cartItems.map((item) => ({
          productId: item.id,
          name: item.name,
          price: item.price,
          quantity: item.quantity,
        })),
        total: calculateTotal(),
      };

      try {
        // Отправляем заказ на сервер
        const token = localStorage.getItem('token'); // Получение токена из localStorage
console.log('Токен:', token); // Логирование токена для проверки

const response = await fetch('http://localhost:5000/api/orders', {
  method: 'POST',
  headers: {
    'Content-Type': 'application/json',
    Authorization: `Bearer ${token}`, // Передача токена в заголовке
  },
  body: JSON.stringify(newOrder),
});


        if (!response.ok) {
          const error = await response.json();
          showNotification(error.message || 'Ошибка при оформлении заказа');
          return;
        }

        const data = await response.json();

        // Обновляем профиль пользователя
        setUserProfile({
          ...userProfile,
          orders: [...(userProfile.orders || []), data.order],
        });

        // Очистка корзины
        clearCart();

        // Уведомление о успешном оформлении заказа
        showNotification('Заказ успешно оформлен!');
      } catch (error) {
        console.error('Ошибка при оформлении заказа:', error);
        showNotification('Ошибка при оформлении заказа');
      }
    } else {
      showNotification('Ваша корзина пуста!');
    }
  };

  return (
    <div className="cart-details">
      <h3>Содержимое корзины</h3>
      {cartItems.length === 0 ? (
        <p>Корзина пуста</p>
      ) : (
        <ul>
          {cartItems.map((item) => (
            <li key={item.id} className="cart-item">
              <img src={item.image} alt={item.name} width="50" />
              <div className="item-details">
                <span>
                  {item.name} — {item.price} ₽, размер: {item.size}
                </span>
                <div className="quantity-controls">
                  <button
                    onClick={() => updateCartItemQuantity(item.id, -1)}
                    className="quantity-button"
                  >
                    -
                  </button>
                  <span>{item.quantity}</span>
                  <button
                    onClick={() => updateCartItemQuantity(item.id, 1)}
                    className="quantity-button"
                  >
                    +
                  </button>
                </div>
              </div>
              <button
                onClick={() => removeFromCart(item.id)}
                className="remove-button"
              >
                Удалить
              </button>
            </li>
          ))}
        </ul>
      )}
      {cartItems.length > 0 && (
        <div className="total">
          <h4>Итоговая стоимость: {calculateTotal()} ₽</h4>
          <button onClick={handleCheckout} className="checkout-button">
            Оформить заказ
          </button>
        </div>
      )}
    </div>
  );
};

export default Cart;
