import React from 'react';
import './UserProfile.css';

const UserProfile = ({ userProfile, updateUserProfile }) => {
  const handleLogout = () => {
    localStorage.removeItem('token');
    updateUserProfile(null);
  };

  return (
    <div className="user-profile-container">
      <h2>Ваш профиль</h2>
      {userProfile ? (
        <>
          <div className="profile-info">
            <p><strong>Имя:</strong> {userProfile.name}</p>
            <p><strong>Email:</strong> {userProfile.email}</p>
          </div>

          <div className="order-history">
            <h3>История заказов</h3>
            {userProfile.orders && userProfile.orders.length > 0 ? (
              <ul>
                {userProfile.orders.map((order) => (
                  <li key={order.id}>
                    <p><strong>Заказ №{order.id}</strong></p>
                    <p>Дата: {order.date}</p>
                    <p>
                      Товары: {order.items.map(item => `${item.name} x${item.quantity}`).join(', ')}
                    </p>
                    <p>Общая сумма: {order.total} ₽</p>
                  </li>
                ))}
              </ul>
            ) : (
              <p>У вас пока нет заказов.</p>
            )}
          </div>

          <button onClick={handleLogout} className="logout-btn">Выйти</button>
        </>
      ) : (
        <p>Загрузка профиля...</p>
      )}
    </div>
  );
};

export default UserProfile;
