import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './LoginForm.css';

const LoginForm = ({ setUserProfile }) => {
  console.log(setUserProfile);
  const [formData, setFormData] = useState({
    email: '',
    password: '',
  });
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setErrorMessage(''); // Сброс ошибки перед новым запросом

    // Проверяем, что поля не пустые
    if (!formData.email || !formData.password) {
      setErrorMessage('Заполните все поля');
      return;
    }

    // Получаем массив пользователей из localStorage
    let users = [];
    try {
      users = JSON.parse(localStorage.getItem('users')) || [];
    } catch (error) {
      console.error('Ошибка при извлечении пользователей:', error);
    }

    // Ищем пользователя с введенными email и паролем
    const user = users.find(
      (user) => user.email === formData.email && user.password === formData.password
    );

    if (user) {
      // Если пользователь найден, сохраняем его профиль в localStorage
      localStorage.setItem('userProfile', JSON.stringify(user));
      setUserProfile(user); // Обновляем состояние userProfile в родительском компоненте
      navigate('/profile'); // Перенаправляем пользователя на страницу профиля
    } else {
      setErrorMessage('Неверный email или пароль'); // Сообщение об ошибке, если данные не совпадают
    }
  };

  return (
    <div className="login-form-container">
      <h2>Вход в профиль</h2>
      {errorMessage && <div className="error-message">{errorMessage}</div>} {/* Отображаем сообщение об ошибке */}
      <form onSubmit={handleSubmit}>
        <div className="form-item">
          <label htmlFor="email">Email:</label>
          <input
            id="email"
            type="email"
            name="email"
            value={formData.email}
            onChange={handleInputChange}
            placeholder="Введите ваш email"
            required
            autoFocus
          />
        </div>
        <div className="form-item">
          <label htmlFor="password">Пароль:</label>
          <input
            id="password"
            type="password"
            name="password"
            value={formData.password}
            onChange={handleInputChange}
            placeholder="Введите ваш пароль"
            required
          />
        </div>
        <button type="submit" className="login-button">
          Войти
        </button>
      </form>
    </div>
  );
};
export default LoginForm;
