import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './LoginForm.css'; // Подключите свой CSS файл для стилизации

const LoginForm = ({ setUserProfile }) => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
  });
  const [errorMessage, setErrorMessage] = useState('');
  const [loading, setLoading] = useState(false); // Стейт для отслеживания загрузки
  const navigate = useNavigate();

  // Обработчик изменения ввода в поля
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  // Обработчик отправки формы
  const handleSubmit = async (e) => {
    e.preventDefault();
    setErrorMessage(''); // Сброс ошибки перед новым запросом

    // Проверка на пустые поля
    if (!formData.email || !formData.password) {
      setErrorMessage('Заполните все поля');
      return;
    }

    setLoading(true); // Включаем индикатор загрузки

    try {
      console.log('Отправка запроса на сервер...');
      const response = await fetch('http://localhost:5000/api/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData), // Отправляем данные формы
      });

      if (!response.ok) {
        const result = await response.json();
        setErrorMessage(result.message || 'Ошибка на сервере');
        return;
      }

      const result = await response.json();
      if (result.success) {
        console.log('Вход успешен, получен профиль:', result.user);
        // Сохраняем данные пользователя и токен в localStorage
        localStorage.setItem('userProfile', JSON.stringify(result.user));
        setUserProfile(result.user); // Обновляем состояние с профилем пользователя
        navigate('/profile'); // Перенаправляем на страницу профиля
      } else {
        setErrorMessage(result.message);
      }
    } catch (error) {
      setErrorMessage('Ошибка при подключении к серверу');
      console.error('Ошибка при отправке запроса:', error);
    } finally {
      setLoading(false); // Останавливаем индикатор загрузки
    }
  };

  return (
    <div className="login-form-container">
      <h2>Вход в профиль</h2>
      {errorMessage && <div className="error-message">{errorMessage}</div>}
      <form onSubmit={handleSubmit}>
        <div className="form-item">
          <label htmlFor="email">Email:</label>
          <input
            id="email"
            type="email"
            name="email"
            value={formData.email}
            onChange={handleInputChange}
            placeholder="Введите ваш email"
            required
            autoFocus
          />
        </div>
        <div className="form-item">
          <label htmlFor="password">Пароль:</label>
          <input
            id="password"
            type="password"
            name="password"
            value={formData.password}
            onChange={handleInputChange}
            placeholder="Введите ваш пароль"
            required
          />
        </div>
        <button type="submit" className="login-button" disabled={loading}>
          {loading ? 'Загрузка...' : 'Войти'}
        </button>
      </form>
    </div>
  );
};

export default LoginForm;
