import React, { useEffect, useState } from 'react';
import ProductCard from './ProductCard';

const ProductList = ({ addToCart, addToWishlist }) => {
  const [products, setProducts] = useState([]); // Состояние для списка товаров
  const [loading, setLoading] = useState(true); // Состояние загрузки
  const [error, setError] = useState(null); // Состояние ошибки

  // Загрузка данных из API
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        setLoading(true);
        const response = await fetch('http://localhost:5000/api/products');
        if (!response.ok) {
          throw new Error(`Ошибка при загрузке товаров: ${response.statusText}`);
        }
        const data = await response.json();
        console.log('Продукты из базы данных:', data);
        setProducts(data);
      } catch (error) {
        console.error('Ошибка при загрузке товаров:', error.message);
        setError('Не удалось загрузить товары. Пожалуйста, попробуйте позже.');
      } finally {
        setLoading(false);
      }
    };

    fetchProducts();
  }, []);

  return (
    <div className="product-list">
      {loading && <p>Загрузка товаров...</p>}
      {error && <p>{error}</p>}
      {!loading && !error && products.length > 0 ? (
        products.map((product) => (
          <ProductCard
            key={product._id} // Используем _id из базы данных
            product={product} // Передаём объект товара
            addToCart={addToCart} // Передаём функцию добавления в корзину
            addToWishlist={addToWishlist} // Передаём функцию добавления в избранное
          />
        ))
      ) : (
        !loading && !error && <p>Товары отсутствуют</p>
      )}
    </div>
  );
};

export default ProductList;
