import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './LoginForm.css';

const LoginForm = ({ setUserProfile }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();

    // Получаем сохраненные данные пользователей из localStorage
    const users = JSON.parse(localStorage.getItem('users')) || [];

    // Проверяем, существует ли пользователь с введенными email и password
    const user = users.find((u) => u.email === email && u.password === password);

    if (user) {
      localStorage.setItem('userProfile', JSON.stringify(user)); // Сохраняем активный профиль
      setUserProfile(user); // Обновляем состояние в приложении
      navigate('/profile'); // Перенаправляем на страницу профиля
    } else {
      setErrorMessage('Неверный email или пароль');
    }
  };

  return (
    <div className="login-form-container">
      <h2>Вход в профиль</h2>
      {errorMessage && <div className="error-message">{errorMessage}</div>}
      <form onSubmit={handleSubmit}>
        <div className="form-item">
          <label htmlFor="email">Email:</label>
          <input
            id="email"
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Введите ваш email"
            required
          />
        </div>
        <div className="form-item">
          <label htmlFor="password">Пароль:</label>
          <input
            id="password"
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Введите ваш пароль"
            required
          />
        </div>
        <button type="submit" className="login-button">
          Войти
        </button>
      </form>
    </div>
  );
};

export default LoginForm;
