import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Register.css';

const Register = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
  });
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Проверка на пустые поля
    if (!formData.name || !formData.email || !formData.password) {
      setError('Пожалуйста, заполните все поля');
      return;
    }

    // Получение текущих пользователей из localStorage
    const users = JSON.parse(localStorage.getItem('users')) || [];

    // Проверка на дублирующийся email
    const emailExists = users.some((user) => user.email === formData.email);
    if (emailExists) {
      setError('Пользователь с таким email уже зарегистрирован');
      return;
    }

    // Добавляем нового пользователя
    const updatedUsers = [...users, formData];
    localStorage.setItem('users', JSON.stringify(updatedUsers)); // Сохраняем массив пользователей

    // Переход на страницу входа
    navigate('/login');
  };

  return (
    <div className="register-container">
      <h2>Регистрация</h2>
      <form onSubmit={handleSubmit} className="register-form">
        <div className="form-group">
          <label>Имя:</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleInputChange}
            placeholder="Введите ваше имя"
          />
        </div>
        <div className="form-group">
          <label>Email:</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleInputChange}
            placeholder="Введите ваш email"
          />
        </div>
        <div className="form-group">
          <label>Пароль:</label>
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleInputChange}
            placeholder="Введите ваш пароль"
          />
        </div>
        {error && <p className="error-message">{error}</p>}
        <button type="submit" className="register-btn">Зарегистрироваться</button>
      </form>
    </div>
  );
};

export default Register;
